mapper.select:



        `comment`.replyId = #{userId}


        and `comment`.isDelete = #{isDelete}


        and `comment`.themeId = #{themeId}


ORDER BY id ASC
LIMIT #{start},#{rows}

SELECT `comment`.id,`comment`.themeId,`comment`.reply,`comment`.replyId, `comment`.replyTime, `user`.`name` as userName, `user`.sex,`user`.age,`user`.icon, (SELECT COALESCE( count( * ) ) FROM zan WHERE zan.targetId = `comment`.id AND zan.type = 1 AND zan.userId = #{userId} ) AS isZan, ( SELECT count( * ) FROM zan WHERE zan.targetId = `comment`.id AND zan.type = 1 ) AS zan FROM `comment` LEFT JOIN `user` on `comment`.replyId = `user`.id
`comment`.themeId = #{themeId}
and `comment`.isDelete = 0
ORDER BY `comment`.replyTime ASC LIMIT #{start},#{rows}
