mapper.select:



        shopId = #{shopId}


        and goods.`serverName` LIKE '%${serverName}%'


        and goods.classify = #{classify}


        and goods.isDelete = #{isDelete}


        and goods.state = #{state}


ORDER BY id ASC
LIMIT #{start},#{rows}

SELECT count(*)
FROM goods


        shopId = #{shopId}


        and goods.`serverName` LIKE '%${serverName}%'


        and goods.classify = #{classify}


        and goods.isDelete = #{isDelete}


        and goods.state = #{state}

SELECT goods.id,goods.shopId, goods.`serverName`,goods.classify, goods.brand,goods.capacity, goods.weight,goods.unit, goods.p1,goods.p2,goods.p3, goods.count,goods.price, goods.image,goods.isDelete, goods.`describe`,goods.isDelete, goods.delReason,shop.shopName, shop.address AS shopAddress, ( SELECT count( * ) FROM orderdetail WHERE orderdetail.targetId = goods.id AND orderdetail.type = 1 ) AS sales, ( SELECT count( * ) FROM collect WHERE collect.targetId = goods.id AND collect.type = 1 ) AS collect, (SELECT COALESCE( count( * ) ) FROM collect WHERE collect.targetId = goods.id AND collect.type = 1 AND collect.userId = #{userId} ) AS isCollect FROM goods LEFT JOIN shop ON goods.shopId = shop.id
goods.`serverName` LIKE '%${serverName}%'
and goods.classify = #{classify}
and goods.brand = #{brand}
and goods.p1 = #{p1}
and goods.p2 = #{p2}
and goods.p3 = #{p3}
and goods.isDelete = 0 and goods.state = 0
ORDER by sales DESC LIMIT #{start},#{rows}

SELECT count(*)
FROM goods


        goods.`serverName` LIKE '%${serverName}%'


        and goods.classify = #{classify}


